const save = (key, value) => {

  console.log('key, value', key, value)
    try {
      localStorage.setItem(key, JSON.stringify(value));
    } catch (err) {
      throw new Error();
    }
  };

  const get = key => {

    console.log('key', key)

    try {
      const items = localStorage.getItem(key);

      console.log('items', items)

      return items ? JSON.parse(items) : null;
    } catch (err) {
      throw new Error();
    }
  };

  export default {
    save,
    get,
  }
